    # sort trains by departure time
    def compare(t1: tuple, t2: tuple) -> bool:
        return t1[0] < t2[0]

    C_S_F.sort(key=lambda x: (x[1] + x[0] + 1) % (C_S_F[0][1] * 2), cmp=compare)

    # find earliest possible time to reach Station N
    def reach_N(station: int, time: int) -> int:
        if station == n:
            return time

        for i in range(station, -1, -1):
            if C_S_F[i][0] <= time:
                if i + 1 == n:
                    return time
                else:
                    return reach_N(i + 1, time)

        return float('inf')

    # calculate earliest possible time to reach Station N
    result = []
    for i in range(n):
        result.append(reach_N(i, 0))

    return '\n'.join(map(str, result))

