        for row in board:
            if not all(0 <= i <= 15 for i in row):
                return False
            for i in range(4):
                if row[i] > row[i+1]:
                    return False
        return True

    def merge_tiles(board):
        for i in range(4):
            for j in range(3):
                if board[i][j] == 0 or board[i][j+1] == 0:
                    continue
                if board[i][j] == board[i][j+1]:
                    board[i][j] = 2 * board[i][j]
                else:
                    board[i][j+1] = 0

    def move_tile(board, direction):
        row, col = 0, 0
        if direction == 0:
            row -= 1
        elif direction == 1:
            row += 1
        elif direction == 2:
            col += 1
        elif direction == 3:
            col -= 1
        return row, col

    def check_collision(board, row, col):
        for i in range(4):
            for j in range(4):
                if board[i][j] == 0:
                    continue
                if i == row and j == col:
                    return True
                if board[i][j] == board[row][col]:
                    return True
        return False

    def is_valid_move(board, direction):
        row, col = move_tile(board, direction)
        if check_collision(board, row, col):
            return False
        return True

    def apply_move(board, direction):
        row, col = move_tile(board, direction)
        board[row][col] = 0

    def print_board(board):
        for row in board:
            print(' '.join(map(str, row)))

    board = [[int(s) for s in line.split()] for line in stdin.split('\n')]
    if not is_valid_state(board):
        return 'Invalid State'

    while True:
        print_board(board)
        direction = int(stdin.split('\n')[4])
        if not is_valid_move(board, direction):
            print('Invalid Move')
            continue
        apply_move(board, direction)
        merge_tiles(board)
        if not is_valid_state(board):
            print('Invalid State')
            return 'Invalid State'
        print_board(board)

    return 'Invalid Input'

