    crossword = [list(line.strip()) for line in stdin.split('\n')[1:]]
    blocked_squares = set(coord for row in crossword for coord, cell in enumerate(row) if cell == '#')

    def is_valid_word(word: str) -> bool:
        return len(word) >= 2 and all(cell not in blocked_squares for cell in word)

    def find_word(row: int, col: int, visited: set, word: str = '') -> str:
        if col >= cols or row >= rows or row < 0:
            return '' if word and not is_valid_word(word) else word

        curr_cell = crossword[row][col]
        if curr_cell == '#' or (curr_cell.isalpha() and curr_cell.islower()) or (curr_cell.isdigit() and curr_cell!= '0') or (curr_cell.isalpha() and curr_cell.isupper()):
            if word and is_valid_word(word):
                return word
            else:
                return find_word(row + 1, col, visited, word + curr_cell)

        if curr_cell == '0':
            return find_word(row, col + 1, visited, word)

        return find_word(row + 1, col, visited, word)

    return find_word(0, 0, set())

def solution(stdin: str) -> str:
    def is_valid_expression(a: int, b: int, c: int, d: int) -> bool:
        if not all(x!= 0 for x in [a, b, c, d]):
            return False

        if c == 0 and d == 0:
            return False

        for op in ["+", "-", "*", "/"]:
            if op == "/" and a!= 0:
                return False

            a_value, b_value = divmod(a, b) if op == "*" or op == "/" else (a, b)
            c_value, d_value = divmod(c, d) if op == "*" or op == "/" else (c, d)

            if (
                (op == "+" and b_value + d_value == c_value)
                or (op == "-" and b_value - d_value == c_value)
                or (op == "*" and c_value * d_value == a_value)
                or (op == "/" and a_value * d_value == c_value)
            ):
                return True

        return False

    a, b, c, d = map(int, stdin.split())

    if not is_valid_expression(a, b, c, d):
        return "problems ahead"

    expressions = [
        f"{a} {op} {b} = {c} {op} {d}"
        for op in ["+", "-", "*", "/"]
        if is_valid_expression(a, b, c, d)
    ]

    return "\n".join(sorted(expressions))


