def solution(stdin: str) -> str:
    M, N = map(int, stdin.split())
    heights = [list(map(int, stdin.split())) for _ in range(M)]

    max_height = max(max(height) for height in heights)
    min_height = min(min(height) for height in heights)

    def bfs(height: int) -> int:
        queue = deque([(0, height, 0)])
        visited = set()
        while queue:
            distance, cur_height, steps = queue.popleft()
            if cur_height == max_height and steps == 0:
                return distance
            if cur_height == min_height and steps == 0:
                return -1
            if cur_height in visited:
                continue
            visited.add(cur_height)
            for neighbor in [(cur_height + 1, distance + 1, steps + 1),
                             (cur_height - 1, distance + 1, steps + 1),
                             (cur_height, distance, steps + 1)]:
                queue.append(neighbor)
        return -1

    shortest_ladder = bfs(0)
    if shortest_ladder == -1:
        return "0"
    return str(shortest_ladder)

