def solution(stdin: str) -> str:
    C, P = map(int, stdin.split())
    initial_heights = list(map(int, stdin.split()))

    def is_on_bottom(heights):
        for h in heights:
            if h!= 0:
                return False
        return True

    def rotate(heights):
        rotated_heights = [0] * C
        for i in range(C):
            rotated_heights[i] = heights[(C - 1) - i]
        return rotated_heights

    def move_left(heights):
        moved_heights = [0] * C
        for i in range(C):
            moved_heights[i] = heights[i - 1]
        return moved_heights

    def move_right(heights):
        moved_heights = [0] * C
        for i in range(C):
            moved_heights[i] = heights[i + 1]
        return moved_heights

    def drop(heights, rotation, move):
        rotated_heights = rotate(heights)
        moved_heights = move(rotated_heights)
        return rotated_heights, moved_heights

    def field_configurations(heights, P, i=1, count=1):
        if is_on_bottom(heights):
            count *= 2 ** i
        if i == P:
            return count
        return field_configurations(heights, P, i + 1, count)

    return str(field_configurations(initial_heights, P))

