    def rating(grid, row, col, seen):
        if col == len(grid[row]) - 1:
            return 1 if seen.count('_') == 0 else 0

        rating = rating(grid, row, col + 1, seen)

        if grid[row][col] == '$' and not seen:
            seen.append('$')
            rating += rating(grid, row, col + 1, seen)
            seen.pop()
        elif grid[row][col] == '_' and seen:
            seen.pop()
            rating += rating(grid, row, col + 1, seen)

        return rating

    return str(rating(grid, 0, 0, []))

