    def binary_search(arr, target):
        left, right = 0, len(arr) - 1
        while left <= right:
            mid = (left + right) // 2
            if arr[mid] == target:
                return mid
            elif arr[mid] < target:
                left = mid + 1
            else:
                right = mid - 1
        return left

    def process_swathers(swathers, stage_times):
        workers = [0] * len(swathers)
        start_time = 0
        for swather in swathers:
            worker_id = binary_search(workers, swather.label)
            if worker_id == len(workers):
                workers.append(swather.label)
            start_time += stage_times[worker_id - 1]
            worker_id -= 1
            while start_time >= stage_times[worker_id]:
                start_time -= stage_times[worker_id]
                worker_id -= 1
            workers[worker_id] = swather.label
        return start_time

    n, m = map(int, stdin.split())
    swathers = []
    for _ in range(n):
        label, *stage_times = map(int, stdin.split())
        swathers.append(Swather(label, stage_times))

