    def can_converse(character: str) -> bool:
        languages = characters[character]
        for other_character in characters.keys():
            if other_character!= character:
                for language in languages:
                    if language in characters[other_character]:
                        return True
        return False

    def can_translate(character: str, source: str, target: str) -> bool:
        for other_character in characters.keys():
            if other_character!= character:
                for language in characters[other_character]:
                    if language!= source:
                        if other_character in characters and language in characters[other_character]:
                            return True
        return False

    def is_small(remaining_characters: set[str]) -> bool:
        if len(remaining_characters) % 2 == 0:
            return False
        for character in characters:
            if can_converse(character):
                return False
        return True

    remaining_characters = set(characters.keys())
    while is_small(remaining_characters):
        for character in remaining_characters:
            if not can_converse(character):
                other_characters = set(characters.keys())
                other_characters.remove(character)
                for other_character in other_characters:
