    def get_adjacent_elements(row, col):
        row_count = len(seating_order)
        col_count = len(seating_order[0])
        adjacent_elements = []
        for dr, dc in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            new_row, new_col = row + dr, col + dc
            if 0 <= new_row < row_count and 0 <= new_col < col_count and seating_order[new_row][new_col]!= '.':
                adjacent_elements.append((new_row, new_col))
        return adjacent_elements

    def count_handshakes(row, col, visited=set()):
        visited.add((row, col))
        adjacent_elements = get_adjacent_elements(row, col)
        for row, col in adjacent_elements:
            if (row, col) in visited:
                continue
            if seating_order[row][col] == 'o':
                count_handshakes(row, col, visited)
        return len(visited)

    morning_handshakes = 0
    for row in range(R):
        for col in range(S):
            if seating_order[row][col] == 'o':
                morning_handshakes += count_handshakes(row, col)
                if morning_handshakes == R * S:
                    break

    return str(morning_handshakes)

