    for i in range(1, N+1):
        A, B = item_drawers[i]
        if A == 0 and B == 0:
            continue
        drawer = 0
        while True:
            visited.add(i, drawer)
            next_drawer = (drawer + 1) % L
            if A == 0:
                item_drawers[i][next_drawer] += 1
                A = next_drawer
            elif B == 0:
                item_drawers[i][next_drawer] += 1
                B = next_drawer
            else:
                for next_i, next_A, next_B in visited:
                    if (next_A == drawer or next_B == drawer) and (next_A, next_B)!= (A, B):
                        if item_drawers[next_i][next_A] > 0 and item_drawers[next_i][next_B] == 0:
                            item_drawers[next_i][next_A] -= 1
                            item_drawers[i][A] += 1
                            A = next_A
                        elif item_drawers[next_i][next_B] > 0 and item_drawers[next_i][next_A] == 0:
                            item_drawers[next_i][next_B] -= 1
                            item_drawers[i][B] += 1
