def solution(stdin: str) -> str:
    key_phrase = stdin.splitlines()[0].strip()
    message = stdin.splitlines()[1].strip()
    plaintext = message.replace(" ", "")

    # generate key table
    key_table = [["" for _ in range(5)] for _ in range(5)]
    key_table[0][0] = key_phrase[0].upper()
    for i in range(1, 5):
        for j in range(5):
            key_table[i][j] = key_table[i-1][j] + key_phrase[i].upper()

    # encrypt message
    encrypted = ""
    for i in range(0, len(plaintext), 2):
        plaintext_pair = plaintext[i:i+2]
        ciphertext_pair = ""
        for letter in plaintext_pair:
            if letter not in "qx":
                row = key_table.index(key_table[key_table.index(letter.upper())])
                col = key_table[row].index(letter.upper())
                if (letter.upper() == key_table[row][col-1] and col > 0) or (letter.upper() == key_table[row][col+1] and col < 4):
                    ciphertext_pair += key_table[row][col]
                else:
                    ciphertext_pair += key_table[row][col+1]
        encrypted += ciphertext_pair

    # remove extra 'x's and spaces
    encrypted = encrypted.replace("X", "")
    encrypted = encrypted.replace(" ", "")
    return encrypted.upper()

def solution(stdin: str) -> str:
    gopher_coordinates = list(map(float, stdin.split()))[:2]
    dog_coordinates = list(map(float, stdin.split()))[-2:]

    gopher_x, gopher_y = gopher_coordinates
    dog_x, dog_y = dog_coordinates

    gopher_holes = []
    while True:
        hole_coordinates = list(map(float, stdin.split()))[-2:]
        gopher_holes.append(hole_coordinates)
        if len(gopher_holes) == 3:
            break

    def distance_between_points(point1: tuple, point2: tuple) -> float:
        x1, y1 = point1
        x2, y2 = point2
        return ((x2 - x1) ** 2 + (y2 - y1) ** 2) ** 0.5

    def closest_hole(point: tuple, hole_coordinates: list) -> tuple:
        closest_distance = float('inf')
        closest_hole = None
        for x, y in hole_coordinates:
            distance = distance_between_points((x, y), point)
            if distance < closest_distance:
                closest_distance = distance
                closest_hole = (x, y)
        return closest_hole

    def direction(point1: tuple, point2: tuple) -> tuple:
        x1, y1 = point1
        x2, y2 = point2
        dx = x2 - x1
        dy = y2 - y1
        return (dy, -dx)

    def move_point(point: tuple, direction: tuple, distance: float) -> tuple:
        x, y = point
        x += distance * direction[0]
        y += distance * direction[1]
        return (x, y)

    def can_escape(dog_x: float, dog_y: float, gopher_holes: list) -> bool:
        closest_hole_x, closest_hole_y = closest_hole((dog_x, dog_y), gopher_holes[0])
        next_hop_x, next_hop_y = move_point((closest_hole_x, closest_hole_y), direction((dog_x, dog_y), (closest_hole_x, closest_hole_y)), 2)

        for i in range(1, len(gopher_holes)):
            hole_coordinates = gopher_holes[i]
            hole_x, hole_y = hole_coordinates
            next_hop_x, next_hop_y = move_point((next_hop_x, next_hop_y), direction((next_hop_x, next_hop_y), (hole_x, hole_y)), 2)

        for i in range(len(gopher_holes) - 1, 0, -1):
            hole_coordinates = gopher_holes[i]
            hole_x, hole_y = hole_coordinates
            next_hop_x, next_hop_y = move_point((next_hop_x, next_hop_y), direction((next_hop_x, next_hop_y), (hole_x, hole_y)), 2)

        if dog_x > next_hop_x:
            return True
        elif dog_x < next_hop_x:
            return False
        elif dog_y > next_hop_y:
            return True
        else:
            return False

    if not can_escape(dog_x, dog_y, gopher_holes):
        return "The gopher cannot escape."
    else:
        return f"The gopher can escape through the hole at ({next_hop_x:.3f}, {next_hop_y:.3f})."

def solution(stdin: str) -> str:
    # Parse input
    a, b, c, d, t = map(int, stdin.strip().split())

    # Check if all streets and avenues have been labelled
    if not (-1000 <= a <= 1000) or not (-1000 <= b <= 1000) or not (-1000 <= c <= 1000) or not (-1000 <= d <= 1000):
        return 'N'

    # Check if there is a street at the starting coordinate
    if a not in range(-1000, 1001):
        return 'N'

    # Check if there is an avenue at the starting coordinate
    if b not in range(-1000, 1001):
        return 'N'

    # Check if there is a street at the destination coordinate
    if c not in range(-1000, 1001):
        return 'N'

    # Check if there is an avenue at the destination coordinate
    if d not in range(-1000, 1001):
        return 'N'

    # Check if there is an intersection at the starting coordinate
    if (a, b) == (c, d):
        return 'N'

    # Check if there is an intersection at the destination coordinate
    if (c, d) == (a, b):
        return 'N'

    # Calculate the number of turns needed to reach the destination coordinate
    turns = abs(a - c) + abs(b - d)

    # Calculate the remaining charge after traveling the required number of turns
    remaining_charge = t - turns * 2

    # Check if the remaining charge is enough to reach the destination coordinate
    if remaining_charge >= 0:
        return 'Y'
    else:
        return 'N'

