    def wash_machine(d, index, machines, color_difference):
        if machines == 0 or index == len(D):
            return min(color_difference, d)

        max_color_difference = 0
        for i in range(index, len(D)):
            if D[i] - D[index] <= K:
                machines_left = machines
                color_difference_left = color_difference
                max_color_difference_left = max_color_difference

                max_color_difference = max(max_color_difference, max_color_difference_left)

                color_difference_left -= max_color_difference_left
                if color_difference_left >= 0:
                    machines_left -= 1

                max_color_difference = max(max_color_difference, wash_machine(d, i + 1, machines_left, color_difference_left))

        return max(max_color_difference, wash_machine(d, index + 1, machines - 1, color_difference))

    return str(wash_machine(0, 0, C, 0))

