        if index > 0 and s[index - 1] == s[index]:
            build_subsequences(subsequence, index + 1)
        else:
            for j in range(index, len(s) + 1):
                subsequence.append(s[index:j])
                build_subsequences(subsequence, j + 1)
                subsequence.pop()

    build_subsequences([], 0)

    # find the subsequences with the lowest cost
    min_cost = float('inf')
    min_subsequence = ''
    for subsequence in S:
        cost = sum(initial_cost[:len(subsequence)])
        if cost < min_cost:
            min_cost = cost
            min_subsequence = subsequence

    if len(min_subsequence) < k:
        return -1

    # find the minimum cost to obtain the set of size k
    min_cost = float('inf')
    min_set = ''
    for subsequence in S:
        if len(subsequence) == k:
            cost = sum(initial_cost[:len(subsequence)])
            if cost < min_cost:
