    if d > n - 1 or k > n - 1:
        return "NO"

    def is_valid(vertex, degrees):
        return vertex not in degrees or degrees[vertex] <= k

    def dijkstra(graph, start):
        inf = float("inf")
        visited = set()
        distances = {vertex: inf for vertex in graph}
        distances[start] = 0

        queue = []
        queue.append((start, distances[start]))

        while queue:
            vertex, distance = queue.pop(0)

            if vertex in visited:
                continue
            visited.add(vertex)

            for neighbor, edge_weight in graph[vertex]:
                new_distance = distance + edge_weight

                if new_distance < distances[neighbor]:
                    distances[neighbor] = new_distance
                    queue.append((neighbor, distances[neighbor]))

        return distances

    def construct_tree(vertices, degree_map, diameter):
        edges = []
        degrees = {}

        for i, vertex in enumerate(vertices):
            degrees[vertex] = degree_map.get(vertex, 0)

        for vertex in vertices:
            for neighbor, edge_weight in graph[vertex]:
                if is_valid(neighbor, degrees):
                    edges.append((vertex, neighbor))

        if not edges:
            return None, None

        if diameter == 0:
            return None, edges

        distances = dijkstra(edges, 1)
        max_distance = max(distances.values())

        if max_distance > diameter:
            return None, None

        for vertex in vertices:
            degree_map[vertex] = degrees[vertex]

        for edge in edges:
            vertex, neighbor = edge
            degree_map[vertex] += 1
            degree_map[neighbor] += 1

        for neighbor in edges:
            vertex, _ = neighbor
            degree_map[vertex] -= 1
            degree_map[neighbor] -= 1

        for vertex in vertices:
            if degree_map[vertex] > k:
                return None, None

        return edges, degree_map

    graph = {}

    for _ in range(n - 1):
        u, v = map(int, stdin.split())
        graph.setdefault(u, []).append((v, 1))
        graph.setdefault(v, []).append((u, 1))

    diameter = d
    degree_map = {vertex: 1 for vertex in range(1, n + 1)}
    edges, degree_map = construct_tree(range(1, n + 1), degree_map, diameter)

    if edges:
        for vertex, neighbor in edges:
            print(f"{vertex} {neighbor}")
    else:
        print("YES")

    return "YES" if edges else "NO"

