    def min_count(count_0, count_1, count_2):
        return min(count_0, min(count_1, count_2))

    def max_count(count_0, count_1, count_2):
        return max(count_0, max(count_1, count_2))

    def count_chars(s, count):
        return s.count('0' * count)

    def replace_chars(s, count_0, count_1, count_2, min_count, max_count):
        replaced_chars = []
        for char in s:
            if char == '0':
                replaced_chars.append('0' * min_count)
            elif char == '1':
                replaced_chars.append('1' * count_1)
            elif char == '2':
                replaced_chars.append('2' * count_2)
            else:
                raise ValueError('Invalid character')
        return ''.join(replaced_chars)

    count_0_s = count_0(s)
    count_1_s = count_1(s)
    count_2_s = count_2(s)

    min_count_0 = count_chars(s, min_count(count_0_s, count_1_s, count_2_s))
    min_count_1 = count_chars(s, count_0_s)
    min_count_2 = count_chars(s, count_1_s)

