    max_count_1 = count_chars(s, count_0_s)
    max_count_2 = count_chars(s, count_1_s)

    min_count_replaced = min(min_count_0, min_count_1, min_count_2)
    max_count_replaced = max(max_count_0, max_count_1, max_count_2)

    min_count_less_than_max_count = min_count_replaced < max_count_replaced

    if min_count_less_than_max_count:
