    def bf(program: str) -> str:
        input_buffer = []
        output_buffer = []
        memory = bytearray(30000)
        pointer = 0
        for line in program.split('\n'):
            if line.startswith('+'):
                pointer += 1
            elif line.startswith('-'):
                pointer -= 1
            elif line.startswith('<'):
                pointer -= int(line[1:])
            elif line.startswith('>'):
                pointer += int(line[1:])
            elif line.startswith('['):
                if pointer == 0:
                    input_buffer.append(memory[pointer])
                else:
                    output_buffer.append(memory[pointer])
            elif line.startswith(']'):
                if pointer == 0:
                    input_buffer.pop()
                else:
                    output_buffer.pop()
            elif line.startswith('.'):
                output_buffer.append(memory[pointer])
            elif line.startswith(','):
                memory[pointer] = input_buffer.pop(0)
            else:
                memory[pointer] = int(line)
        return ''.join(chr(x) for x in output_buffer)

    expression = stdin.strip()
    program = ''
    for sign in '+-*/<>,.[]':
        i = expression.find(sign)
        if i!= -1:
            program += expression[:i]
            program += bf(expression[i:])
            expression = expression[i+1:]
    program += expression
    return program

