def solution(stdin: str) -> str:
    n, m = map(int, stdin.split()[0:2])
    grid = [list(row.strip()) for row in stdin.split()[2:]]

    def check_grid(grid):
        for i in range(n):
            for j in range(m):
                if grid[i][j] == '*':
                    if i < n-1 and grid[i+1][j] == '*':
                        return False
                    if j < m-1 and grid[i][j+1] == '*':
                        return False
                    if i > 0 and grid[i-1][j] == '*':
                        return False
                    if j > 0 and grid[i][j-1] == '*':
                        return False
        return True

    def draw_star(x, y, s):
        for i in range(s):
            for j in range(s):
                if i == 0 or i == s-1 or j == 0 or j == s-1:
                    print('*', end='')
                else:
                    print('.', end='')
            print()

    def draw_stars(stars):
        x_list = sorted(set(star[0] for star in stars))
        y_list = sorted(set(star[1] for star in stars))
        for x in x_list:
            for y in y_list:
                s_list = sorted(set(star[2] for star in stars if star[0] == x and star[1] == y))
                if len(s_list) > 0:
                    s = s_list[0]
                    draw_star(x, y, s)

    stars = []
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*':
                stars.append((i, j, 1))
                if check_grid(grid):
                    continue
                stars.append((i, j, 2))
                if check_grid(grid):
                    continue
                stars.append((i, j, 3))
                if check_grid(grid):
                    continue
                return "-1"

    draw_stars(stars)

