def solution(stdin: str) -> str:
    n, m = map(int, stdin.split())
    edges = []
    for i in range(m):
        u, v = map(int, stdin.split())
        edges.append((u, v))

    # Bellman-Ford algorithm to find shortest path from each vertex to all other vertices
    dist = [-1] * (n + 1)
    dist[1] = 0
    for i in range(n - 1):
        for u, v in edges:
            if dist[u]!= -1 and dist[u] + 1 < dist[v]:
                dist[v] = dist[u] + 1

    # Find negative cycles
    for u, v in edges:
        if dist[u]!= -1 and dist[u] + 1 < dist[v]:
            return "NO"

    # Find positive cycles
    for u, v in edges:
        if dist[u]!= -1 and dist[u] + 1 == dist[v]:
            return "NO"

    # Direct edges if necessary
    result = []
    for u, v in edges:
        if dist[u]!= -1 and dist[u] + 1 == dist[v]:
            result.append("0")
        else:
            result.append("1")
    result = "".join(result)

    return "YES" + result

