def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    p, q = stdin.split()[1:n+1]
    p, q = [int(x) for x in p.split()], [int(x) for x in q.split()]

    def permute(arr):
        res = []
        for i in range(len(arr)):
            res.append(arr[i])
        for i in range(len(res)):
            for j in range(i + 1, len(res)):
                res[i], res[j] = res[j], res[i]
        return res

    def smallest_permutation(arr):
        if len(arr) == 1:
            return arr
        else:
            res = []
            for i in range(len(arr)):
                res.append(arr[i])
            for i in range(len(res)):
                for j in range(i + 1, len(res)):
                    res[i], res[j] = res[j], res[i]
            return res

    def count_inversions(arr1, arr2):
        n1, n2 = len(arr1), len(arr2)
        i, j = 0, 0
        count = 0
        while i < n1 and j < n2:
            if arr1[i] <= arr2[j]:
                count += n1 - i
                i += 1
            else:
                count += n2 - j
                j += 1
        return count

    def lexicographical_difference(arr1, arr2):
        arr1.sort()
        arr2.sort()
        return count_inversions(arr1, arr2)

    p_sorted = permute(p)
    q_sorted = permute(q)

    if p_sorted!= q_sorted:
        return str(lexicographical_difference(p_sorted, q_sorted))
    else:
        return str(lexicographical_difference(p, q))

