def solution(stdin: str) -> str:
    n, m = map(int, stdin.split())
    grid = [list(row) for row in stdin.split('\n') if row.strip()]

    def is_valid_star(x: int, y: int) -> bool:
        return 0 < x <= n and 0 < y <= m

    def is_valid_cell(x: int, y: int) -> bool:
        return '.' in grid[x - 1][y - 1]

    def is_valid_size(x: int) -> bool:
        return 0 < x

    def find_star(x: int, y: int, size: int) -> tuple[int, int]:
        x_ray = x - size // 2
        y_ray = y - size // 2

        while True:
            if not is_valid_cell(x_ray, y_ray) or not is_valid_size(size):
                return None

            if is_valid_star(x_ray, y_ray):
                return x_ray, y_ray

            size += 1
            x_ray = x - size // 2
            y_ray = y - size // 2

    def draw_stars(x: int, y: int, size: int) -> None:
        while is_valid_star(x, y) and is_valid_size(size):
            if is_valid_cell(x, y):
                grid[x - 1][y - 1] = '*'
            x, y, size = find_star(x, y, size)

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if is_valid_cell(i, j):
                draw_stars(i, j, 1)
            elif '.' not in grid[i - 1][j - 1]:
                draw_stars(i, j, 1)
            else:
                draw_stars(i, j, 2)

    result = []
    for row in grid:
        result.append(' '.join(row))

    return '\n'.join(result)


