    def find_min(arr, start, end):
        min_index = start
        for i in range(start + 1, end + 1):
            if arr[i] < arr[min_index]:
                min_index = i
        return min_index

    def swap(arr, i, j):
        arr[i], arr[j] = arr[j], arr[i]

    def sort_array(arr, p):
        n = len(arr)
        for i in range(n - 1):
            min_index = find_min(arr, i, n - 1)
            if min_index!= i:
                swap(arr, min_index, i)
            if arr[i] > arr[i + 1]:
                return False
        return True

    def is_valid_swap(arr, p, i, j):
        return p[j - 1] < i <= p[j - 1]

    def can_sort(arr, p):
        for i in range(len(arr)):
            if is_valid_swap(arr, p, i, i + 1):
                continue
            for j in range(len(p)):
                if is_valid_swap(arr, p, i, p[j - 1]) or is_valid_swap(arr, p, i, p[j]):
                    continue
                if not is_valid_swap(arr, p, p[j - 1], p[j - 1] + 1):
                    return False
            if not is_valid_swap(arr, p, i, i + 1):
                return False
        return True

