def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    a = list(map(int, stdin.split()[1:]))

    def find_adjacent(i, j):
        return ((i+1) % n, j), ((i-1) % n, j), ((i, j+1) % n, j), ((i, j-1) % n, j)

    def process_operation(t, i, j):
        if t == 1:
            a[i] += abs(a[i] - a[j])
        else:
            a[i] -= abs(a[i] - a[j])

    def process_adjacent_operations(i, j):
        adjacent = find_adjacent(i, j)
        for t, x, y, z in adjacent:
            process_operation(t, x, y)
            process_operation(t, x, z)
            process_operation(t, y, z)

    count = 0
    while any(a):
        min_index = a.index(min(a))
        process_adjacent_operations(min_index, min_index)
        count += 1

    return str(count) + "\n" + " ".join([str(t) + " " + str(i) + " " + str(j) for t, i, j in find_adjacent(0, 0)])

