def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    a = list(map(int, stdin.split()[1:]))

    def merge_sort(arr: list) -> list:
        if len(arr) <= 1:
            return arr
        mid = len(arr) // 2
        left = merge_sort(arr[:mid])
        right = merge_sort(arr[mid:])
        return merge(left, right)

    def merge(left: list, right: list) -> list:
        result = []
        i = j = 0
        while i < len(left) and j < len(right):
            if left[i] < right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1
        result.extend(left[i:])
        result.extend(right[j:])
        return result

    a = merge_sort(a)

    inc = []
    dec = []
    inc_count = 0
    dec_count = 0

    for i in range(1, len(a)):
        if a[i] < a[i-1]:
            inc.append(a[i-1])
            inc_count += 1
        else:
            dec.append(a[i-1])
            dec_count += 1

    inc = merge_sort(inc)
    dec = merge_sort(dec)

    if inc_count == 0 and dec_count == 0:
        return "NO"

    n_inc = len(inc)
    n_dec = len(dec)

    if n_inc + n_dec!= n:
        return "NO"

    if n_inc == 0:
        inc_str = ""
    else:
        inc_str = str(n_inc) + " "
        for num in inc:
            inc_str += str(num) + " "

    if n_dec == 0:
        dec_str = ""
    else:
        dec_str = str(n_dec) + " "
        for num in dec:
            dec_str += str(num) + " "

    return "YES" + "\n" + inc_str + "\n" + dec_str

