def solution(stdin: str) -> str:
    n, k = map(int, stdin.split())
    s = list(stdin.split()[2])

    def get_beads_count(beads):
        return sum(1 for _ in beads if _ in s)

    beads_count = get_beads_count(s)
    if beads_count < k:
        return 0

    def is_k_beautiful(beads, start, k):
        return all(s[i] in beads for i in range(start, start + k))

    def dfs(beads, start, current_beads_count, k):
        if start == len(beads):
            return current_beads_count == k

        if not is_k_beautiful(beads, start, k):
            return dfs(beads, start + 1, current_beads_count, k)

        next_beads = beads[:start] + beads[start + 1:]
        return dfs(next_beads, start, current_beads_count + 1, k)

    return str(dfs(s, 0, 0, k))

