    from typing import List
    import sys

    class ListNode:
        def __init__(self, val=0, next=None):
            self.val = val
            self.next = next

    def detect_cycle(head: ListNode) -> bool:
        if not head:
            return False

        slow = head
        fast = head

        while fast is not None and fast.next is not None:
            slow = slow.next
            fast = fast.next.next

            if slow == fast:
                break

        if fast is None or fast.next is None:
            return False

        slow = head
        while slow!= fast:
            slow = slow.next
            fast = fast.next

        return True

    def is_valid_index(head: ListNode, index: int) -> bool:
        if not head:
            return False

        current = head
        for _ in range(index):
            current = current.next
            if not current:
                return False

        return True

    def get_cycle_start_node(head: ListNode) -> ListNode:
        if not head or not is_valid_index(head, 1):
            return None

        current = head
        cycle_start = None
        while current.next:
            current = current.next
            if current.next == head:
                cycle_start = current
                break

        return cycle_start

    def print_linked_list(head: ListNode) -> None:
        if not head:
            return

        current = head
        while current:
            print(current.val, end=" ")
            current = current.next

        print()

    def read_linked_list() -> ListNode:
        input_data = stdin.split()
        input_data = [int(x) for x in input_data]

        if not input_data:
            return None

        head = ListNode(input_data[0])
        current = head
        for val in input_data[1:]:
            current.next = ListNode(val)
            current = current.next

        return head

    def main() -> None:
        head = read_linked_list()
        pos = int(stdin.split()[1]) - 1

        if detect_cycle(head):
            print("true")
            if pos == -1:
                cycle_start = head
            else:
                cycle_start = get_cycle_start_node(head)
                pos_in_cycle = 0
                current = cycle_start
                while current:
                    if pos_in_cycle == pos:
                        break
                    current = current.next
                    pos_in_cycle += 1

            if cycle_start:
                print("Cycle start node:", cycle_start.val)
            else:
                print("No cycle")
        else:
            print("false")

    if len(sys.argv) > 1:
        stdin = sys.stdin.read()

    main()

