def solution(stdin: str) -> str:
    # Convert input word to set of characters
    word = set(stdin.lower())

    # Remove duplicates and convert to list
    words = list(set(stdin.lower()))

    # Sort the words in lexicographic order
    words.sort()

    # Check if the length of the sorted list is 1
    if len(words) == 1:
        return words[0]

    # Check if the length of the sorted list is 2
    if len(words) == 2:
        # Check if the words are anagrams
        if sorted(words[0]) == sorted(words[1]):
            return words[0]
        else:
            return -1

    # Check for multigrams
    for i in range(len(words) - 1):
        # Check if the words are anagrams
        if sorted(words[i]) == sorted(words[i + 1]):
            # Check if the current word is a multigram
            for j in range(i + 2, len(words)):
                # Check if the next words are also anagrams
                if sorted(words[j]) == sorted(words[j - 1]):
                    # If the next words are not anagrams, skip them
                    if not sorted(words[j]) == sorted(words[j - 1]):
                        break
                    # Check if the current word is a multigram with the next word as root
                    if sorted(words[i]) == sorted(words[j]):
                        return words[i]

    # If no multigram found, return -1
    return -1

