def solution(stdin: str) -> str:
    # parse input
    m, n = map(int, stdin.split())
    heights = []
    for i in range(m):
        heights.append(list(map(int, stdin.split())))

    # find the height of the special coin
    special_height = 0
    for i in range(m):
        for j in range(n):
            if heights[i][j] == 0:
                special_height = i
                break
        if special_height!= 0:
            break

    # find the shortest ladder
    shortest_ladder = float('inf')
    for length in range(1, special_height + 1):
        stack = [special_height]
        visited = set()
        while stack:
            current_height = stack.pop()
            if current_height == special_height:
                break
            visited.add(current_height)
            for direction in [0, 1, 0, -1]:
                new_height = current_height + direction
                if new_height < 0 or new_height >= m:
                    continue
                if new_height not in visited:
                    stack.append(new_height)
        if len(stack) >= length:
            shortest_ladder = min(shortest_ladder, length)

    # output the result
    return str(shortest_ladder)

