    # Base case
    if P == 1:
        return '1'

    # Recursive case
    def count_ways(col_heights, height, piece_num):
        if height == C:
            return 1

        ways = 0
        for piece_rotation in range(1, 4):
            # Rotate and move piece
            if piece_rotation == 2:
                piece_num = (piece_num - 1) % 7
            elif piece_rotation == 3:
                piece_num = (piece_num + 1) % 7
            piece_height = piece_heights[piece_num]

            # Check if piece can be placed
            if height + piece_height > C:
                break
            for i in range(piece_height):
                if i >= height:
                    if col_heights[height - i] == 0:
                        col_heights[height - i] = 1
                        ways += count_ways(col_heights, height + i, piece_num)
                        col_heights[height - i] = 0
                    else:
                        break

        return ways

    return str(count_ways(col_heights, 0, P - 1))

