def solution(stdin: str) -> str:
    N = int(stdin.split()[0])
    positions = list(map(lambda x: list(map(int, x.split())), stdin.split()[1:]))
    positions.sort(key=lambda x: (x[0], -x[1]))

    def is_valid(positions):
        n = len(positions)
        for i in range(n):
            x1, y1 = positions[i]
            for j in range(i + 1, n):
                x2, y2 = positions[j]
                if abs(x1 - x2) == abs(y1 - y2):
                    return False
        return True

    def is_valid_attack(positions):
        n = len(positions)
        for i in range(n):
            x1, y1 = positions[i]
            for j in range(i + 1, n):
                x2, y2 = positions[j]
                if abs(x1 - x2) == abs(y1 - y2) or abs(x1 - x2) + abs(y1 - y2) == abs(y1 - y2) - abs(x1 - x2):
                    return False
        return True

    def get_valid_positions(positions):
        valid_positions = []
        for i in range(N):
            for j in range(i + 1, N):
                if (i, j) not in positions:
                    valid_positions.append((i, j))
        return valid_positions

    def place_queen(positions, x, y):
        return positions[:x] + [(x, y)] + positions[x + 1:]

    def attack(positions, x1, y1, x2, y2):
        n = len(positions)
        for i in range(n):
            x, y = positions[i]
            if abs(x1 - x) == abs(y1 - y) or abs(x1 - x) + abs(y1 - y) == abs(y1 - y) - abs(x1 - x):
                return True
        return False

    def backtrack(positions, x, y, attacked, attack_positions):
        if x == N:
            if attacked:
                return
            if is_valid(positions):
                return "CORRECT"
            return "INCORRECT"
        for i in range(N):
            if i not in attack_positions and not attack(positions, y, i, x, i):
                positions = place_queen(positions, x, i)
                attacked = True
                attack_positions.append(i)
                result = backtrack(positions, x + 1, i, attacked, attack_positions)
                if result is not None:
                    return result
                positions = positions[:x] + [(x, i)] + positions[x + 1:]
                attacked = False
                attack_positions.pop()

    positions = get_valid_positions(positions)
    return backtrack(positions, 0, 0, False, [])

