def solution(stdin: str) -> str:
    n = int(stdin.split('\n')[0])
    triangles = []
    for i in range(n):
        sideA, sideB, sideC = map(int, stdin.split('\n')[i+1].split())
        triangles.append((sideA, sideB, sideC))

    def min_length(triangles, rod_length):
        stack = [(0, 0, rod_length, rod_length)]
        while stack:
            x, y, a, b = stack.pop()
            if a > b:
                a, b = b, a

            if a >= b:
                return a

            if y == b - 1:
                return a

            if x + 1 == a:
                stack.append((x + 1, y, a, b))
                continue

            if x + 1 == b:
                stack.append((x + 1, y, a, b))
                continue

            if y + 1 == b:
                stack.append((x, y + 1, a, b))
                continue

            if y + 1 == a:
                stack.append((x, y + 1, a, b))
                continue

            min_x, min_y = min(x, y), min(a, b)
            max_x, max_y = max(x, y), max(a, b)
            stack.append((min_x, min_y, max_x, max_y))

        return 0

    def area(a, b, c):
        return 0.5 * abs(a * b + b * c + c * a)

    def ornament_area(a, b, c, length):
        return 0.5 * (length * (a + b + c) - area(a, b, c))

    def total_area(triangles):
        return sum(area(*triangle) for triangle in triangles)

    min_area = float('inf')
    for i in range(1, 10000):
        rod_length = i
        triangles_with_length = [(triangle[0], triangle[1], triangle[2], rod_length) for triangle in triangles]
        min_length_triangles = [(triangle[0], triangle[1], triangle[2], min_length(triangles_with_length, rod_length)) for triangle in triangles]
        total_area_triangles = sum(ornament_area(*triangle, rod_length) for triangle in min_length_triangles)
        if total_area_triangles < min_area:
            min_area = total_area_triangles

    return f"{min_area:.4f}"

