def solution(stdin: str) -> str:
    # define a function to check if a string is a palindrome
    def is_palindrome(s: str) -> bool:
        return s == s[::-1]

    # define a function to check if two strings are anagrams
    def is_anagram(s1: str, s2: str) -> bool:
        if len(s1)!= len(s2):
            return False
        s1_count = [0] * 26
        s2_count = [0] * 26
        for char in s1:
            s1_count[ord(char) - ord('a')] += 1
        for char in s2:
            s2_count[ord(char) - ord('a')] -= 1
        for count in s1_count:
            if count!= 0:
                return False
        for count in s2_count:
            if count!= 0:
                return False
        return True

    # read the input string
    s = stdin.strip()

    # check if the string is a palindrome
    if is_palindrome(s):
        return "0"

    # check if the string is an anagram of a palindrome
    for i in range(1, len(s)):
        for j in range(i + 1, len(s) + 1):
            sub_s = s[i:j]
            if is_anagram(s, sub_s) and is_palindrome(sub_s):
                return "0"

    # check if the string is an anagram of any palindrome
    for i in range(len(s)):
        for j in range(i + 1, len(s) + 1):
            sub_s = s[i:j]
            if is_anagram(s, sub_s):
                return j - i

    return "0"

