    segments = []
    for _ in range(N):
        D, theta = map(int, input().split())
        segments.append((D, theta))

    def calc_velocity(segment, start, end):
        if start == end:
            return 0
        theta = segments[start][1] - segments[end][1]
        if theta < 0:
            theta += 180
        return g * math.cos(math.radians(theta)) * (segments[end][0] - start) / (end - start)

    return '\n'.join(map(str, [calc_velocity(segment, 0, N - 1) for segment in segments]))

