    while True:
        # Find the next coffee shop to place
        next_coffee_shop = None
        for row in grid:
            for cell, value in zip(row, row):
                if value == 'E' and next_coffee_shop is None:
                    next_coffee_shop = cell, row.index(cell)

        # If there are no more coffee shops to place, break
        if next_coffee_shop is None:
            break

        # Mark the next coffee shop as being placed
        coffee_shop, index = next_coffee_shop
        grid[index][grid[index].index(coffee_shop)] = '#'

        # Remove the coffee shop from the map
        coffee_shops_placed -= 1

        # Check if there are more coffee shops to place
        for row in grid:
            for cell, value in zip(row, row):
                if value == '.' and cell == '#':
                    row[row.index(cell)] = 'E'

