        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if 0 <= nx < a and 0 <= ny < b:
                if (nx, ny) == (c, d):
                    return cost
                if abs(nx - x) + abs(ny - y) == 1:
                    cost += 1
                    cost = dfs(nx, ny, pos + 1, cost)
                    cost -= 1
        return cost
    return 'Y' if dfs(a, b, 0, 0) == 0 else 'N'

