def solution(stdin: str) -> str:
    events = []
    for line in stdin.split('\n'):
        tokens = line.split()
        if tokens[0] == 'buy':
            events.append(('buy', int(tokens[1]), float(tokens[2])))
        elif tokens[0] =='sell':
            events.append(('sell', int(tokens[1]), float(tokens[2])))
        elif tokens[0] =='split':
            events.append(('split', int(tokens[1])))
        elif tokens[0] =='merge':
            events.append(('merge', int(tokens[1])))
        elif tokens[0] == 'die':
            events.append(('die', float(tokens[1])))

    shares = 0
    profit = 0
    profit_per_share = 0

    for event in events:
        if event[0] == 'buy':
            shares += event[1]
            profit_per_share += event[2]
        elif event[0] =='sell':
            shares -= event[1]
            profit += event[2]
        elif event[0] =='split':
            shares *= 2
            profit += shares * event[2] - shares * profit_per_share
            profit_per_share = 0
        elif event[0] =='merge':
            shares //= event[1]
            profit += shares * event[2] - shares * profit_per_share
            profit_per_share = 0
        elif event[0] == 'die':
            profit += event[1]
            shares = 0

    tax_rate = 0.3
    taxes = shares * (profit - profit_per_share) * tax_rate
    final_profit = profit + taxes

    return f"{final_profit:.2f}"

