def solution(stdin: str) -> str:
    rows, cols = map(int, stdin.split()[0].split())
    grid = []

    for _ in range(rows):
        row = list(stdin.strip())
        grid.append(row)

    land_count = sum(1 for row in grid for cell in row if cell == 'L')
    water_count = sum(1 for row in grid for cell in row if cell == 'W')

    if land_count > water_count:
        return "0"
    else:
        def dfs(i, j, island):
            grid[i][j] = 'C'
            island.append((i, j))
            if i > 0 and grid[i - 1][j] == 'L':
                dfs(i - 1, j, island)
            if i < rows - 1 and grid[i + 1][j] == 'L':
                dfs(i + 1, j, island)
            if j > 0 and grid[i][j - 1] == 'L':
                dfs(i, j - 1, island)
            if j < cols - 1 and grid[i][j + 1] == 'L':
                dfs(i, j + 1, island)

        islands = []
        for i in range(rows):
            for j in range(cols):
                if grid[i][j] == 'L':
                    dfs(i, j, islands)

        return str(len(islands))

