    return str(days - 1)

def solution(stdin: str) -> str:
    n = int(stdin)
    if n < 0 or n > 2**32-1:
        raise ValueError("Input out of range")

    def h(n):
        if n == 1:
            return 1
        elif n % 2 == 0:
            return n + h(n//2)
        else:
            return n + h(3*n + 1)

    h_sequence = [h(n)]
    for i in range(n):
        if h(n) in h_sequence:
            h_sequence.remove(h(n))
        else:
            h_sequence.append(h(n))
            n = h(n)

    return str(sum(h_sequence))

def solution(stdin: str) -> str:
    n, *s = map(int, stdin.split())
    if not (2 <= n <= 30):
        return "impossible"
    safe_orders = set()
    for i in range(1, n + 1):
        temp = set(s[:i])
        for j in range(i, n + 1):
            if len(temp) >= j:
                temp.remove(j)
                temp.add(j - i)
        if len(temp) == 0:
            safe_orders.add(i)
    if len(safe_orders) == 0:
        return "impossible"
    for order in safe_orders:
        if all(len(temp) >= order for temp in s):
            return str(order)
    return "impossible"

