import sys
from typing import List

class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, p):
        if self.parent[p]!= p:
            self.parent[p] = self.find(self.parent[p])
        return self.parent[p]

    def union(self, p, q):
        root_p = self.find(p)
        root_q = self.find(q)
        if root_p!= root_q:
            if self.rank[root_p] > self.rank[root_q]:
                self.parent[root_q] = root_p
            else:
                self.parent[root_p] = root_q
                if self.rank[root_p] == self.rank[root_q]:
                    self.rank[root_q] += 1

class Graph:
    def __init__(self, n):
        self.edges = []
        self.n = n

    def add_edge(self, u, v, w):
        self.edges.append((u, v, w))

    def kruskal(self):
        uf = UnionFind(self.n)
        self.edges.sort(key=lambda x: x[2])
        cost = 0
        for u, v, w in self.edges:
            if uf.find(u)!= uf.find(v):
                uf.union(u, v)
                cost += w
        return cost

    def max_cost(self, labelled_edges):
        uf = UnionFind(self.n)
        labelled_edges.sort(key=lambda x: x[2])
        cost = 0
