    from collections import defaultdict
    from heapq import heappush, heappop

    def dijkstra(graph, start):
        dist = defaultdict(lambda: float('inf'))
        dist[start] = 0
        heap = [(0, start, start)]

        while heap:
            (d, node, prev) = heappop(heap)

            if d > dist[node]:
                continue

            for neighbor, weight in graph[node].items():
                if weight + d < dist[neighbor]:
                    dist[neighbor] = weight + d
                    heappush(heap, (weight + d, neighbor, node))

        return dist

    n = int(stdin.splitlines()[0])
    graph = defaultdict(dict)

    for i in range(1, n):
        a, b = map(int, stdin.splitlines()[i].split())
        graph[a][b] = 1
        graph[b][a] = 1

    distances = dijkstra(graph, 1)
    max_edges = 0
    for i in range(2, n):
        edges_count = distances[i] + distances[i]
        max_edges = max(max_edges, edges_count)

    a, b, c = 1, 2, 3
    return str(max_edges) + "\n" + str(a) + " " + str(b) + " " + str(c)

