    def helper(row, used, path_sum, k):
        if row == n:
            return path_sum
        for j in range(m):
            if not used[j] and (path_sum + a[row][j]) % k == 0:
                used[j] = True
                path_sum += a[row][j]
                res = helper(row + 1, used, path_sum, k)
                if res != -1:
                    return res
                used[j] = False
                path_sum -= a[row][j]
        return -1

    return helper(0, [False] * m, 0, k)

