def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    f = list(map(int, stdin.splitlines()[1].split()))
    assert len(f) == n

    def find_giver(i: int, f: list, visited: set) -> int:
        if i in visited:
            return -1
        visited.add(i)
        if f[i] != 0:
            return f[i]
        for j in range(n):
            if j != i and f[j] != 0:
                result = find_giver(f[j] - 1, f, visited)
                if result != -1:
                    return result
        return -1

    def find_receiver(i: int, f: list, visited: set) -> int:
        if i in visited:
            return -1
        visited.add(i)
        if f[i] != 0:
            return i + 1
        for j in range(n):
            if j != i and f[j] == 0:
                result = find_receiver(j, f, visited)
                if result != -1:
                    return result
        return -1

    for i in range(n):
        if f[i] == 0:
            giver = find_giver(i, f, set())
            if giver == -1:
                return "impossible"
            f[i] = giver
            receiver = find_receiver(giver - 1, f, set())
            if receiver == -1:
                return "impossible"
            f[giver - 1] = receiver

    result = [f[i] * n for i in range(n)]
    return " ".join(map(str, result))

