    def knap_sack(projects: list, capacity: int, index: int) -> int:
        if index == 0 or capacity == 0:
            return 0

        if projects[index - 1][0] > capacity:
            return knap_sack(projects, capacity, index - 1)

        include_item = projects[index - 1][1] + knap_sack(projects, capacity - projects[index - 1][0], index - 1)
        exclude_item = knap_sack(projects, capacity, index - 1)
        return max(include_item, exclude_item)

    def max_subset_size(projects: list, rating: int) -> int:
        def binary_search(projects: list, capacity: int) -> int:
            left, right = 0, rating
            while left <= right:
                mid = (left + right) // 2
                if knap_sack(projects, mid, len(projects)) <= capacity:
                    left = mid + 1
                else:
                    right = mid - 1
            return right

        max_size = 0
        for i in range(len(projects)):
            subset_rating = rating - sum(projects[j][0] for j in range(i))
            if subset_rating >= 0:
                capacity = subset_rating
                max_size = max(max_size, binary_search(projects[:i], capacity))
        return max_size

