        pivot_sum = subarray_sum // 2
        left, right = 0, pivot_sum
        while left <= right:
            pivot = (left + right) // 2
            if sum(subarray[:pivot]) == pivot_sum:
                return pivot
            elif sum(subarray[:pivot]) < pivot_sum:
                left = pivot + 1
            else:
                right = pivot - 1
        return -1

    def binary_search(subarray, target_sum):
        left, right = 0, len(subarray) - 1
        while left <= right:
            mid = (left + right) // 2
            if sum(subarray[mid:]) == target_sum:
                return mid
            elif sum(subarray[mid:]) < target_sum:
                left = mid + 1
            else:
                right = mid - 1
        return -1

