        visited[index] = True
        memo[index] = max(memo[index], helper(reposted_pictures, index + 1, visited, memo))
        visited[index] = False

        return memo[index]

    reposted_pictures = [-1] * n
    visited = [False] * n
    memo = [-1] * n
    result = helper(reposted_pictures, 0, visited, memo)

    if result == -1:
        return "-1"
    else:
        return str(result)


