    i = n - 1
    while i >= 0:
        prev_index = find_prev(i)
        result.append(a[prev_index])
        i = prev_index

    return " ".join(map(str, result[::-1]))

def solution(stdin: str) -> str:
    n = int(input())
    a = list(map(int, input().split()))

    def find_previous(x: int) -> int:
        if x % 2 == 1:
            return x - 1
        else:
            return x // 2

    def is_valid(i: int, x: int, a: list) -> bool:
        if i == 0:
            return True
        return x // 2 == a[i - 1] or x == a[i - 1] * 2

    def backtrack(i: int, x: int, a: list) -> bool:
        if i == n:
            return True
        for j in range(2 * max(x - 1, 1), x + 1):
            if is_valid(i, j, a):
                a[i] = j
                if backtrack(i + 1, j, a):
                    return True
        a[i] = 0
        return False

    def solve(a: list) -> list:
        if not backtrack(0, a[0], a):
            return []
        return a

    result = solve(a)
    if not result:
        return "No solution"
    return " ".join(map(str, result))


