    H, W, K = map(int, stdin.split())
    grid = []
    for _ in range(H):
        grid.append(stdin.readline().strip())

    def count_black_squares(rows: set, cols: set) -> int:
        count = 0
        for i in range(H):
            for j in range(W):
                if (i+1 in rows or i+1 in cols) or (j+1 in rows or j+1 in cols):
                    continue
                if grid[i][j] == '#':
                    count += 1
        return count

    def count_choices(rows: set, cols: set) -> int:
        count = 0
        for i in range(1, H+1):
            for j in range(1, W+1):
                if i in rows or j in cols:
                    continue
                new_rows = rows.copy()
                new_cols = cols.copy()
                new_rows.add(i)
                new_cols.add(j)
                if count_black_squares(new_rows, new_cols) == K:
                    count += 1
        return count

    choices = []
    for rows in itertools.combinations(range(1, H+1), K):
        for cols in itertools.combinations(range(1, W+1), K):
            if len(rows) + len(cols) == K:
                choices.append((rows, cols))

    result = 0
    for rows, cols in choices:
        result += count_choices(set(rows), set(cols))

    return result

