        ways = 1
        for count in letter_count:
            if count > 1:
                ways *= factorial(count) // (factorial(count - 1) * count)
        return ways

    def factorial(n: int) -> int:
        result = 1
        for i in range(1, n + 1):
            result *= i
        return result

    ways = 0
    for letter in "MARC":
        ways += count_ways(letter)

    print(ways)


