    a_set = set(a)
    a_max = max(a)
    a_min = min(a)
    a_range = a_max - a_min
    a_sum = sum(a)

    if len(a_set) == 1:
        return str(0)

    if a_range == 0:
        return str(a_sum // n)

    if a_sum % n != 0:
        return str(-1)

    D = a_sum // n - a_min
    return str(D)

