def solution(stdin: str) -> str:
    n, x, y = map(int, stdin.split())
    num = int(stdin.split("\n")[1], 2)

    def remainder(num: int, x: int, y: int) -> int:
        return num % (10**y // 10**(y - x))

    if remainder(num, x, y) == 1:
        return "0"

    min_operations = float("inf")
    for i in range(n):
        if num & (1 << i):
            continue
        new_num = num | (1 << i)
        operations = 1
        while remainder(new_num, x, y) != 1:
            new_num ^= (1 << operations)
            operations += 1
        min_operations = min(min_operations, operations)

    return str(min_operations)


